import React from 'react'
import "./post.css"

export default function Post(props) {
  return (
    <div className='post' style={{backgroundColor:props.color}}>
        <p id="titel">{props.titel} </p>
        <p id="info">{props.info} </p>
        

    </div>
  )
}
