import React from 'react'
import "./post.css"

export default function Post(props) {
  return (
    <div className='post' style={{backgroundColor:props.color}}>
        <h1>Name: {props.name} </h1>
        <h2>Post: {props.body} </h2>
    </div>
  )
}
