import './App.css';
import Header from './components/Header';
import Middel from './components/Middel';
import Last from './components/Last';
import Post from './components/Post';

function App() {
  let postArr = [
    { color: "darkcyan", name: "John wick", body: "never touch my dog" },
    { color: "blueviolet", name: "Tuna", body: "my best singer" },
    { color: "salmon", name: "Eminem", body: "the best" },
  ];


return (
  <div className="App">
    <Header />
    <Middel />

    <Last />
    <Post name={postArr[0].name} body={postArr[0].body} color={postArr[0].color} />
    <Post name={postArr[1].name} body={postArr[1].body} color={postArr[1].color} />
    <Post name={postArr[2].name} body={postArr[2].body} color={postArr[2].color} />
{postArr.map((value)=>{
return <Post name={value.name} body={value.body} color={value.color} />
})}
</div>
);
};

export default App;
